import React, { useState } from 'react';
import axios from 'axios';
import SampleModal from './SampleModal';
import './SampleRegistrationForm.css';

const SampleRegistrationForm = () => {
  const [formData, setFormData] = useState({
    registrationNumber: 'PAFDA-25-000001',
    registrationType: 'Self / Individual',
    panelCompany: '',
    senderDetails: { name: '', designation: '', department: '', province: '', division: '', district: '', streetNo: '', area: '', address: '', contactNumber: '', cnic: '', email: '' },
    companyDetails: { companyName: '', companyContact: '', companyEmail: '', companyAddress: '' },
    focalPersonDetails: { name: '', contactNumber: '', designation: '', email: '' },
    deliveredVia: { method: 'Self', name: '', cnic: '', contactNumber: '', streetNo: '', area: '', address: '', companyName: '', companyContact: '', companyAddress: '', parcelTrackingNumber: '', dispatchDate: '', remarks: '' },
    packagingDetails: { packagingType: '', sealCondition: '', packageType: '', labelDetails: '', packagingNotes: '' },
    samples: [],
    grandTotal: 0,
  });
  const [showSampleModal, setShowSampleModal] = useState(false);

  const handleInputChange = (e, section, field) => {
    setFormData(prev => ({
      ...prev,
      [section]: { ...prev[section], [field]: e.target.value },
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/registrations', formData);
      alert('Registration submitted successfully!');
      setFormData({
        registrationNumber: 'PAFDA-25-000001',
        registrationType: 'Self / Individual',
        panelCompany: '',
        senderDetails: { name: '', designation: '', department: '', province: '', division: '', district: '', streetNo: '', area: '', address: '', contactNumber: '', cnic: '', email: '' },
        companyDetails: { companyName: '', companyContact: '', companyEmail: '', companyAddress: '' },
        focalPersonDetails: { name: '', contactNumber: '', designation: '', email: '' },
        deliveredVia: { method: 'Self', name: '', cnic: '', contactNumber: '', streetNo: '', area: '', address: '', companyName: '', companyContact: '', companyAddress: '', parcelTrackingNumber: '', dispatchDate: '', remarks: '' },
        packagingDetails: { packagingType: '', sealCondition: '', packageType: '', labelDetails: '', packagingNotes: '' },
        samples: [],
        grandTotal: 0,
      });
    } catch (error) {
      console.error(error);
      alert('Error submitting registration.');
    }
  };

  const addSample = (sample) => {
    setFormData(prev => ({
      ...prev,
      samples: [...prev.samples, sample],
      grandTotal: prev.grandTotal + sample.totalPrice,
    }));
  };

  return (
    <div className="form-container">
      <h2>Sample Registration Form</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-section">
          <label>Registration Number: {formData.registrationNumber}</label>
        </div>
        <div className="form-section">
          <h3>Registration Type</h3>
          <select
            value={formData.registrationType}
            onChange={(e) => setFormData({ ...formData, registrationType: e.target.value })}
          >
            <option value="Self / Individual">Self / Individual</option>
            <option value="Panel Company">Panel Company</option>
          </select>
          {formData.registrationType === 'Panel Company' && (
            <select
              value={formData.panelCompany}
              onChange={(e) => setFormData({ ...formData, panelCompany: e.target.value })}
            >
              <option value="">-- Select Panel Company --</option>
              <option>Nestle Pakistan Limited</option>
              <option>Unilever Pakistan Limited</option>
              <option>Engro Foods Limited</option>
              <option>Shan Foods Private Limited</option>
              <option>National Foods Limited</option>
              <option>Coca-Cola Beverages Pakistan Limited</option>
              <option>PepsiCo International Pakistan</option>
              <option>Shezan International Limited</option>
            </select>
          )}
        </div>
        <div className="form-section">
          <h3>Sender Details</h3>
          <input
            type="text"
            placeholder="Name *"
            value={formData.senderDetails.name}
            onChange={(e) => handleInputChange(e, 'senderDetails', 'name')}
            required
          />
          <input
            type="text"
            placeholder="Designation *"
            value={formData.senderDetails.designation}
            onChange={(e) => handleInputChange(e, 'senderDetails', 'designation')}
            required
          />
          {/* Add other sender details inputs as in the previous version */}
        </div>
        {/* Include other sections (Company Details, Focal Person Details, Delivered Via, Packaging Details) as in the previous version */}
        <div className="form-section">
          <h3>Samples Management</h3>
          <button type="button" onClick={() => setShowSampleModal(true)}>
            Add Sample
          </button>
          <div>
            {formData.samples.map((sample, index) => (
              <div key={index}>
                <p>Sample {index + 1}: {sample.sampleType} - Rs. {sample.totalPrice}</p>
              </div>
            ))}
          </div>
          <p>Grand Total: Rs. {formData.grandTotal.toFixed(2)}</p>
        </div>
        <div className="form-actions">
          <button type="submit">Preview</button>
          <button type="button" onClick={() => setFormData({
            registrationNumber: 'PAFDA-25-000001',
            registrationType: 'Self / Individual',
            panelCompany: '',
            senderDetails: { name: '', designation: '', department: '', province: '', division: '', district: '', streetNo: '', area: '', address: '', contactNumber: '', cnic: '', email: '' },
            companyDetails: { companyName: '', companyContact: '', companyEmail: '', companyAddress: '' },
            focalPersonDetails: { name: '', contactNumber: '', designation: '', email: '' },
            deliveredVia: { method: 'Self', name: '', cnic: '', contactNumber: '', streetNo: '', area: '', address: '', companyName: '', companyContact: '', companyAddress: '', parcelTrackingNumber: '', dispatchDate: '', remarks: '' },
            packagingDetails: { packagingType: '', sealCondition: '', packageType: '', labelDetails: '', packagingNotes: '' },
            samples: [],
            grandTotal: 0,
          })}>
            Cancel
          </button>
        </div>
      </form>
      {showSampleModal && (
        <SampleModal
          onClose={() => setShowSampleModal(false)}
          onAddSample={addSample}
        />
      )}
    </div>
  );
};

export default SampleRegistrationForm;
