import React, { useState } from 'react';
import axios from 'axios';
import './SampleRegistrationForm.css';

const SampleRegistrationForm = () => {
  const [formData, setFormData] = useState({
    registrationNumber: 'PAFDA-25-000001',
    registrationType: 'Self / Individual',
    panelCompany: '',
    senderDetails: { 
      name: '', 
      designation: '', 
      department: '', 
      province: '', 
      division: '', 
      district: '', 
      streetNo: '', 
      area: '', 
      address: '', 
      contactNumber: '', 
      cnic: '', 
      email: '' 
    },
    companyDetails: { 
      companyName: '', 
      companyContact: '', 
      companyEmail: '', 
      companyAddress: '' 
    },
    focalPersonDetails: { 
      name: '', 
      contactNumber: '', 
      designation: '', 
      email: '' 
    },
    deliveredVia: { 
      method: 'Self', 
      name: '', 
      cnic: '', 
      contactNumber: '', 
      streetNo: '', 
      area: '', 
      address: '', 
      companyName: '', 
      companyContact: '', 
      companyAddress: '', 
      parcelTrackingNumber: '', 
      dispatchDate: '', 
      remarks: '' 
    },
    packagingDetails: { 
      packagingType: '', 
      sealCondition: '', 
      packageType: '', 
      labelDetails: '', 
      packagingNotes: '' 
    },
    samples: [],
    grandTotal: 0,
  });

  const handleInputChange = (e, section, field) => {
    const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
    setFormData(prev => ({
      ...prev,
      [section]: { ...prev[section], [field]: value },
    }));
  };

  const handleAddressChange = (e, section, field) => {
    setFormData(prev => ({
      ...prev,
      [section]: { ...prev[section], [field]: e.target.value },
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/registrations', formData);
      alert('Registration submitted successfully!');
      setFormData({
        registrationNumber: 'PAFDA-25-000001',
        registrationType: 'Self / Individual',
        panelCompany: '',
        senderDetails: { 
          name: '', 
          designation: '', 
          department: '', 
          province: '', 
          division: '', 
          district: '', 
          streetNo: '', 
          area: '', 
          address: '', 
          contactNumber: '', 
          cnic: '', 
          email: '' 
        },
        companyDetails: { 
          companyName: '', 
          companyContact: '', 
          companyEmail: '', 
          companyAddress: '' 
        },
        focalPersonDetails: { 
          name: '', 
          contactNumber: '', 
          designation: '', 
          email: '' 
        },
        deliveredVia: { 
          method: 'Self', 
          name: '', 
          cnic: '', 
          contactNumber: '', 
          streetNo: '', 
          area: '', 
          address: '', 
          companyName: '', 
          companyContact: '', 
          companyAddress: '', 
          parcelTrackingNumber: '', 
          dispatchDate: '', 
          remarks: '' 
        },
        packagingDetails: { 
          packagingType: '', 
          sealCondition: '', 
          packageType: '', 
          labelDetails: '', 
          packagingNotes: '' 
        },
        samples: [],
        grandTotal: 0,
      });
    } catch (error) {
      console.error(error);
      alert('Error submitting registration.');
    }
  };

  const addSample = (sample) => {
    setFormData(prev => ({
      ...prev,
      samples: [...prev.samples, sample],
      grandTotal: prev.grandTotal + sample.totalPrice,
    }));
  };

  return (
    <div className="form-container">
      <h2>Sample Registration Form</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-section">
          <label>Registration Number: {formData.registrationNumber}</label>
        </div>

        <div className="form-section">
          <h3>Registration Type</h3>
          <select
            value={formData.registrationType}
            onChange={(e) => handleInputChange(e, 'registrationType', 'value')}
          >
            <option value="Self / Individual">Self / Individual</option>
            <option value="Panel Company">Panel Company</option>
          </select>
          {formData.registrationType === 'Panel Company' && (
            <div>
              <label>Panel Company:</label>
              <input
                type="text"
                value={formData.panelCompany}
                onChange={(e) => handleInputChange(e, 'panelCompany', 'value')}
              />
            </div>
          )}
        </div>

        <div className="form-section">
          <h3>Sender Details</h3>
          <div className="address-fields">
            <input
              type="text"
              placeholder="Name"
              value={formData.senderDetails.name}
              onChange={(e) => handleAddressChange(e, 'senderDetails', 'name')}
            />
            <input
              type="text"
              placeholder="Designation"
              value={formData.senderDetails.designation}
              onChange={(e) => handleAddressChange(e, 'senderDetails', 'designation')}
            />
            <input
              type="text"
              placeholder="Department"
              value={formData.senderDetails.department}
              onChange={(e) => handleAddressChange(e, 'senderDetails', 'department')}
            />
            <input
              type="text"
              placeholder="Province"
              value={formData.senderDetails.province}
              onChange={(e) => handleAddressChange(e, 'senderDetails', 'province')}
            />
            <input
              type="text"
              placeholder="Division"
              value={formData.senderDetails.division}
              onChange={(e) => handleAddressChange(e, 'senderDetails', 'division')}
            />
            <input
              type="text"
              placeholder="District"
              value={formData.senderDetails.district}
              onChange={(e) => handleAddressChange(e, 'senderDetails', 'district')}
            />
            <input
              type="text"
              placeholder="Street No"
              value={formData.senderDetails.streetNo}
              onChange={(e) => handleAddressChange(e, 'senderDetails', 'streetNo')}
            />
            <input
              type="text"
              placeholder="Area"
              value={formData.senderDetails.area}
              onChange={(e) => handleAddressChange(e, 'senderDetails', 'area')}
            />
            <input
              type="text"
              placeholder="Address"
              value={formData.senderDetails.address}
              onChange={(e) => handleAddressChange(e, 'senderDetails', 'address')}
            />
            <input
              type="text"
              placeholder="Contact Number"
              value={formData.senderDetails.contactNumber}
              onChange={(e) => handleAddressChange(e, 'senderDetails', 'contactNumber')}
            />
            <input
              type="text"
              placeholder="CNIC"
              value={formData.senderDetails.cnic}
              onChange={(e) => handleAddressChange(e, 'senderDetails', 'cnic')}
            />
            <input
              type="email"
              placeholder="Email"
              value={formData.senderDetails.email}
              onChange={(e) => handleAddressChange(e, 'senderDetails', 'email')}
            />
          </div>
        </div>

        <div className="form-section">
          <h3>Company Details</h3>
          <div className="address-fields">
            <input
              type="text"
              placeholder="Company Name"
              value={formData.companyDetails.companyName}
              onChange={(e) => handleAddressChange(e, 'companyDetails', 'companyName')}
            />
            <input
              type="text"
              placeholder="Company Contact"
              value={formData.companyDetails.companyContact}
              onChange={(e) => handleAddressChange(e, 'companyDetails', 'companyContact')}
            />
            <input
              type="email"
              placeholder="Company Email"
              value={formData.companyDetails.companyEmail}
              onChange={(e) => handleAddressChange(e, 'companyDetails', 'companyEmail')}
            />
            <input
              type="text"
              placeholder="Company Address"
              value={formData.companyDetails.companyAddress}
              onChange={(e) => handleAddressChange(e, 'companyDetails', 'companyAddress')}
            />
          </div>
        </div>

        <div className="form-section">
          <h3>Focal Person Details</h3>
          <div className="address-fields">
            <input
              type="text"
              placeholder="Name"
              value={formData.focalPersonDetails.name}
              onChange={(e) => handleAddressChange(e, 'focalPersonDetails', 'name')}
            />
            <input
              type="text"
              placeholder="Contact Number"
              value={formData.focalPersonDetails.contactNumber}
              onChange={(e) => handleAddressChange(e, 'focalPersonDetails', 'contactNumber')}
            />
            <input
              type="text"
              placeholder="Designation"
              value={formData.focalPersonDetails.designation}
              onChange={(e) => handleAddressChange(e, 'focalPersonDetails', 'designation')}
            />
            <input
              type="email"
              placeholder="Email"
              value={formData.focalPersonDetails.email}
              onChange={(e) => handleAddressChange(e, 'focalPersonDetails', 'email')}
            />
          </div>
        </div>

        <div className="form-section">
          <h3>Delivered Via</h3>
          <div className="address-fields">
            <select
              value={formData.deliveredVia.method}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'method')}
            >
              <option value="Self">Self</option>
              <option value="Courier">Courier</option>
              <option value="Other">Other</option>
            </select>
            <input
              type="text"
              placeholder="Name"
              value={formData.deliveredVia.name}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'name')}
            />
            <input
              type="text"
              placeholder="CNIC"
              value={formData.deliveredVia.cnic}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'cnic')}
            />
            <input
              type="text"
              placeholder="Contact Number"
              value={formData.deliveredVia.contactNumber}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'contactNumber')}
            />
            <input
              type="text"
              placeholder="Street No"
              value={formData.deliveredVia.streetNo}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'streetNo')}
            />
            <input
              type="text"
              placeholder="Area"
              value={formData.deliveredVia.area}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'area')}
            />
            <input
              type="text"
              placeholder="Address"
              value={formData.deliveredVia.address}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'address')}
            />
            <input
              type="text"
              placeholder="Company Name"
              value={formData.deliveredVia.companyName}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'companyName')}
            />
            <input
              type="text"
              placeholder="Company Contact"
              value={formData.deliveredVia.companyContact}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'companyContact')}
            />
            <input
              type="text"
              placeholder="Company Address"
              value={formData.deliveredVia.companyAddress}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'companyAddress')}
            />
            <input
              type="text"
              placeholder="Parcel Tracking Number"
              value={formData.deliveredVia.parcelTrackingNumber}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'parcelTrackingNumber')}
            />
            <input
              type="date"
              placeholder="Dispatch Date"
              value={formData.deliveredVia.dispatchDate}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'dispatchDate')}
            />
            <textarea
              placeholder="Remarks"
              value={formData.deliveredVia.remarks}
              onChange={(e) => handleAddressChange(e, 'deliveredVia', 'remarks')}
            />
          </div>
        </div>

        <div className="form-section">
          <h3>Packaging Details</h3>
          <div className="address-fields">
            <input
              type="text"
              placeholder="Packaging Type"
              value={formData.packagingDetails.packagingType}
              onChange={(e) => handleAddressChange(e, 'packagingDetails', 'packagingType')}
            />
            <input
              type="text"
              placeholder="Seal Condition"
              value={formData.packagingDetails.sealCondition}
              onChange={(e) => handleAddressChange(e, 'packagingDetails', 'sealCondition')}
            />
            <input
              type="text"
              placeholder="Package Type"
              value={formData.packagingDetails.packageType}
              onChange={(e) => handleAddressChange(e, 'packagingDetails', 'packageType')}
            />
            <input
              type="text"
              placeholder="Label Details"
              value={formData.packagingDetails.labelDetails}
              onChange={(e) => handleAddressChange(e, 'packagingDetails', 'labelDetails')}
            />
            <textarea
              placeholder="Packaging Notes"
              value={formData.packagingDetails.packagingNotes}
              onChange={(e) => handleAddressChange(e, 'packagingDetails', 'packagingNotes')}
            />
          </div>
        </div>

        <div className="form-section sample-section">
          <h3>Sample Details</h3>
          <div className="sample-list">
            <table>
              <thead>
                <tr>
                  <th>Sample Name</th>
                  <th>Quantity</th>
                  <th>Price</th>
                  <th>Total</th>
                </tr>
              </thead>
              <tbody>
                {formData.samples.map((sample, index) => (
                  <tr key={index}>
                    <td>{sample.name}</td>
                    <td>{sample.quantity}</td>
                    <td>{sample.price}</td>
                    <td>{sample.totalPrice}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="grand-total">
            <h3>Grand Total:</h3>
            <span>{formData.grandTotal}</span>
          </div>
        </div>

        <button
          type="submit"
          className="submit-button"
        >
          Submit Registration
        </button>
      </form>
    </div>
  );
};

export default SampleRegistrationForm;
